interface ButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: 'primary' | 'secondary' | 'ghost' | 'danger';
  size?: 'sm' | 'md' | 'lg';
  children: React.ReactNode;
}

export function Button({ variant = 'primary', size = 'md', children, className, ...props }: ButtonProps) {
  const base = 'inline-flex items-center justify-center font-bold transition-all cursor-pointer';

  const variants = {
    primary: 'bg-orange-400 text-slate-900 shadow-[4px_4px_0px_#FFF] hover:translate-y-0.5 hover:shadow-[2px_2px_0px_#FFF]',
    secondary: 'bg-slate-700 text-white border border-slate-600 hover:border-orange-400',
    ghost: 'text-slate-400 hover:text-orange-400',
    danger: 'bg-red-500 text-white shadow-[4px_4px_0px_#FFF] hover:translate-y-0.5 hover:shadow-[2px_2px_0px_#FFF]',
  };

  const sizes = {
    sm: 'px-3 py-1.5 text-sm',
    md: 'px-6 py-2 text-base',
    lg: 'px-8 py-3 text-lg',
  };

  return (
    <button className={`${base} ${variants[variant]} ${sizes[size]} ${className || ''}`} {...props}>
      {children}
    </button>
  );
}

import { LucideIcon } from 'lucide-react';

interface IconButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  icon: LucideIcon;
  variant?: 'default' | 'ghost' | 'danger';
  size?: 'sm' | 'md' | 'lg';
}

export function IconButton({ icon: Icon, variant = 'default', size = 'md', className, ...props }: IconButtonProps) {
  const variants = {
    default: 'bg-slate-800 border border-slate-700 text-slate-300 hover:border-orange-400 hover:text-orange-400',
    ghost: 'text-slate-400 hover:text-orange-400 hover:bg-slate-800',
    danger: 'bg-slate-800 border border-slate-700 text-red-400 hover:bg-red-500 hover:text-white hover:border-red-500',
  };

  const sizes = {
    sm: 'p-1.5',
    md: 'p-2',
    lg: 'p-3',
  };

  const iconSizes = { sm: 'w-4 h-4', md: 'w-5 h-5', lg: 'w-6 h-6' };

  return (
    <button className={`transition-all cursor-pointer ${variants[variant]} ${sizes[size]} ${className || ''}`} {...props}>
      <Icon className={iconSizes[size]} />
    </button>
  );
}

interface InputProps extends React.InputHTMLAttributes<HTMLInputElement> {
  label?: string;
  error?: string;
}

export function Input({ label, error, className, ...props }: InputProps) {
  return (
    <div className="flex flex-col gap-1.5">
      {label && (
        <label className="text-xs font-mono uppercase text-slate-500">
          {label}
        </label>
      )}
      <input
        className={`
          w-full bg-slate-800 border-2 text-slate-100
          px-4 py-3 font-mono
          placeholder:text-slate-500
          focus:outline-none focus:border-orange-400 focus:ring-2 focus:ring-orange-400/20
          transition-all
          ${error ? 'border-red-500' : 'border-slate-700'}
          ${className || ''}
        `}
        {...props}
      />
      {error && <span className="text-sm text-red-400 font-mono">{error}</span>}
    </div>
  );
}

