interface AvatarProps {
  src?: string;
  alt?: string;
  fallback?: string;
  size?: 'sm' | 'md' | 'lg';
}

export function Avatar({ src, alt, fallback, size = 'md' }: AvatarProps) {
  const sizes = {
    sm: 'w-8 h-8 text-xs',
    md: 'w-10 h-10 text-sm',
    lg: 'w-14 h-14 text-base',
  };

  if (src) {
    return (
      <img
        src={src}
        alt={alt || ''}
        className={`${sizes[size]} rounded-full object-cover border-2 border-slate-700`}
      />
    );
  }

  return (
    <div className={`${sizes[size]} rounded-full bg-slate-700 border-2 border-slate-600 flex items-center justify-center font-bold text-orange-400`}>
      {fallback || '?'}
    </div>
  );
}

import { AlertCircle, CheckCircle, Info, XCircle } from 'lucide-react';

interface AlertProps {
  type?: 'info' | 'success' | 'warning' | 'error';
  title?: string;
  children: React.ReactNode;
}

export function Alert({ type = 'info', title, children }: AlertProps) {
  const config = {
    info: { icon: Info, bg: 'bg-cyan-400/10', border: 'border-cyan-400/30', text: 'text-cyan-400' },
    success: { icon: CheckCircle, bg: 'bg-green-400/10', border: 'border-green-400/30', text: 'text-green-400' },
    warning: { icon: AlertCircle, bg: 'bg-yellow-400/10', border: 'border-yellow-400/30', text: 'text-yellow-400' },
    error: { icon: XCircle, bg: 'bg-red-400/10', border: 'border-red-400/30', text: 'text-red-400' },
  };

  const { icon: Icon, bg, border, text } = config[type];

  return (
    <div className={`${bg} ${border} border p-4 flex gap-3`}>
      <Icon className={`w-5 h-5 ${text} shrink-0 mt-0.5`} />
      <div>
        {title && <h4 className={`font-bold ${text} mb-1`}>{title}</h4>}
        <p className="text-sm text-slate-300">{children}</p>
      </div>
    </div>
  );
}

import { X, CheckCircle, AlertCircle, Info } from 'lucide-react';

interface ToastProps {
  type?: 'success' | 'error' | 'info';
  message: string;
  onClose?: () => void;
}

export function Toast({ type = 'info', message, onClose }: ToastProps) {
  const icons = {
    success: <CheckCircle className="w-5 h-5 text-green-400" />,
    error: <AlertCircle className="w-5 h-5 text-red-400" />,
    info: <Info className="w-5 h-5 text-cyan-400" />,
  };

  return (
    <div className="bg-slate-800 border border-slate-700 shadow-lg px-4 py-3 flex items-center gap-3 min-w-[300px]">
      {icons[type]}
      <span className="flex-1 text-sm">{message}</span>
      {onClose && (
        <button onClick={onClose} className="text-slate-400 hover:text-white cursor-pointer">
          <X className="w-4 h-4" />
        </button>
      )}
    </div>
  );
}

import { useState } from 'react';

interface Tab {
  id: string;
  label: string;
  content: React.ReactNode;
}

interface TabsProps {
  tabs: Tab[];
  defaultTab?: string;
}

export function Tabs({ tabs, defaultTab }: TabsProps) {
  const [active, setActive] = useState(defaultTab || tabs[0]?.id);

  return (
    <div>
      <div className="flex gap-2 border-b border-slate-700 pb-2 mb-4">
        {tabs.map((tab) => (
          <button
            key={tab.id}
            onClick={() => setActive(tab.id)}
            className={`px-4 py-2 font-mono text-sm transition-all cursor-pointer ${
              active === tab.id
                ? 'bg-orange-400 text-slate-900'
                : 'text-slate-400 hover:text-orange-400'
            }`}
          >
            {tab.label}
          </button>
        ))}
      </div>
      <div>
        {tabs.find((tab) => tab.id === active)?.content}
      </div>
    </div>
  );
}

import { ChevronRight } from 'lucide-react';

interface BreadcrumbItem {
  label: string;
  href?: string;
}

interface BreadcrumbProps {
  items: BreadcrumbItem[];
}

export function Breadcrumb({ items }: BreadcrumbProps) {
  return (
    <nav className="flex items-center gap-2 text-sm font-mono">
      {items.map((item, index) => (
        <div key={index} className="flex items-center gap-2">
          {index > 0 && <ChevronRight className="w-4 h-4 text-slate-600" />}
          {item.href ? (
            <a href={item.href} className="text-slate-400 hover:text-orange-400 transition-colors">
              {item.label}
            </a>
          ) : (
            <span className="text-orange-400">{item.label}</span>
          )}
        </div>
      ))}
    </nav>
  );
}

interface ProgressProps {
  value: number;
  max?: number;
  showLabel?: boolean;
  variant?: 'default' | 'success' | 'warning' | 'danger';
}

export function Progress({ value, max = 100, showLabel = false, variant = 'default' }: ProgressProps) {
  const percentage = Math.min(100, Math.max(0, (value / max) * 100));

  const variants = {
    default: 'bg-orange-400',
    success: 'bg-green-400',
    warning: 'bg-yellow-400',
    danger: 'bg-red-400',
  };

  return (
    <div className="w-full">
      {showLabel && (
        <div className="flex justify-between mb-2 text-sm font-mono">
          <span className="text-slate-400">Progress</span>
          <span className="text-orange-400">{Math.round(percentage)}%</span>
        </div>
      )}
      <div className="h-2 bg-slate-700 overflow-hidden">
        <div
          className={`h-full ${variants[variant]} transition-all duration-300`}
          style={{ width: `${percentage}%` }}
        />
      </div>
    </div>
  );
}

interface SkeletonProps {
  className?: string;
  variant?: 'text' | 'circular' | 'rectangular';
}

export function Skeleton({ className, variant = 'text' }: SkeletonProps) {
  const base = 'bg-slate-700 animate-pulse';

  const variants = {
    text: 'h-4 rounded',
    circular: 'rounded-full',
    rectangular: 'rounded',
  };

  return <div className={`${base} ${variants[variant]} ${className || ''}`} />;
}

