@import url('https://fonts.googleapis.com/css2?family=Space+Grotesk:wght@400;500;600;700&family=JetBrains+Mono:wght@400;500;600&display=swap');
@import "tailwindcss";

:root {
  /* Core Colors */
  --background: #0F172A;
  --surface: #1E293B;
  --surface-hover: #334155;
  --border: #334155;

  /* Brand Colors */
  --primary: #F97316;
  --primary-light: #FB923C;
  --primary-glow: rgba(249, 115, 22, 0.3);

  /* Accent Colors */
  --accent-blue: #38BDF8;
  --accent-yellow: #FEF3C7;

  /* Text Colors */
  --foreground: #F8FAFC;
  --muted: #94A3B8;
  --muted-dark: #64748B;

  /* Fonts */
  --font-sans: 'Space Grotesk', system-ui, sans-serif;
  --font-mono: 'JetBrains Mono', monospace;
}

/* Cut Corners Utility */
.cut-corners {
  clip-path: polygon(
    15px 0, 100% 0,
    100% calc(100% - 15px),
    calc(100% - 15px) 100%,
    0 100%,
    0 15px
  );
}

.cut-corners-sm {
  clip-path: polygon(
    8px 0, 100% 0,
    100% calc(100% - 8px),
    calc(100% - 8px) 100%,
    0 100%,
    0 8px
  );
}

/* Katana Button */
.btn-katana {
  position: relative;
  background: var(--primary);
  color: var(--background);
  font-weight: 600;
  padding: 0.75rem 1.5rem;
  box-shadow: 4px 4px 0px #FFFFFF;
  transition: all 0.15s ease;
  cursor: pointer;
}

.btn-katana:hover {
  transform: translate(2px, 2px);
  box-shadow: 2px 2px 0px #FFFFFF;
}

/* Grid Background */
.grid-bg {
  background-image: linear-gradient(rgba(51, 65, 85, 0.08) 1px, transparent 1px),
                    linear-gradient(90deg, rgba(51, 65, 85, 0.08) 1px, transparent 1px);
  background-size: 40px 40px;
}

/* Ronin Sun */
.ronin-sun {
  position: fixed;
  top: 10%;
  left: 50%;
  transform: translateX(-50%);
  width: 80vh;
  height: 80vh;
  background: linear-gradient(180deg, #FB923C 0%, #F43F5E 100%);
  border-radius: 50%;
  z-index: -1;
  opacity: 0.12;
  filter: blur(80px);
  pointer-events: none;
}

